sys_variable_value.value:
(function execute(inputs, outputs) {

	var certificateRequest = inputs.certificateRequest;
  	var requestType = certificateRequest.u_certificate_request_type;
  	var installed = certificateRequest.u_install_on;
  	var assignmentGroup ='';

  	if(requestType.indexOf('external') != -1){
    	assignmentGroup = gs.getProperty('x_unugr_certificat.certrequest.external.assignment.group');  
    }else {
      	assignmentGroup = gs.getProperty('x_unugr_certificat.certrequest.internal.assignment.group'); 
    }


	var assignmentGroupRecord = new GlideRecord('sys_user_group');
	assignmentGroupRecord.addQuery('name',assignmentGroup);
	assignmentGroupRecord.query();

	if(assignmentGroupRecord.next()){
		outputs.assignmentGroup = assignmentGroupRecord;
	}  	


})(inputs, outputs);

