sys_variable_value.value:
(function execute(inputs, outputs) {

  	var certificateRequest = inputs.certificateRequest;

    var installOnArray = (certificateRequest.u_install_on.toString()).split(',');



  	gs.info('install on Array ' + installOnArray);
  	var installTargets = new GlideRecord('x_unugr_certificat_installation_targets');
  	installTargets.addQuery('sys_id','IN',installOnArray);
  	installTargets.orderBy('u_install_order');
  	installTargets.setLimit(1);
  	installTargets.query();

  	gs.info('record count ' + installTargets.getRowCount());

  	if(installTargets.next()){
     	outputs.responsibleForCSR = installTargets;
    }



})(inputs, outputs);

