sys_script_fix.script:
// Fix script to check checkboxes for Installed On to cert requests

var installedOn;
var outputLog = 'Log Begin - STRY0074361\n';
var errorOccured = false;

outputLog += 'Add checkboxes for Installed On to cert requests\n';

try {

	var grCertReq = new GlideRecord('x_unugr_certificat_certificate_request');
	grCertReq.query();

	//gs.info("Record Count " + grCertReq.getRowCount());

	while (grCertReq.next()) {
		//gs.info(grCertReq.number.toString());
		var certReqInstalledOn = grCertReq.u_install_on.toString().split(",");
		//gs.info(certReqInstalledOn);
		for (var instOnIndex in certReqInstalledOn) {
			installedOn = new GlideRecord('x_unugr_certificat_installation_targets');
			installedOn.addQuery('sys_id=' + certReqInstalledOn[instOnIndex]);
			installedOn.query();
			while (installedOn.next()) {
				//gs.info("target to find: " + installedOn.u_target_value);
				switch (installedOn.u_target_value.toString()) {
					case 'f5':
						//gs.info('F5 Found');
						grCertReq.u_inston_f5 = true;
						break;
					case 'aws':
						//gs.info('aws Found');
						grCertReq.u_inston_aws = true;
						break;
					case 'api_gateway':
						//gs.info('API Gateway Found');
						grCertReq.u_inston_api_gateway = true;
						break;
					case 'iis':
						//gs.info('IIS Found');
						grCertReq.u_inston_iis = true;
						break;
					case 'server':
						//gs.info('Server Found');
						grCertReq.u_inston_server = true;
						break;
					case 'vendor_server':
						//gs.info('Vendor Server Found');
						grCertReq.u_inston_vendor_server = true;
						break;
				}

				grCertReq.update();


			}

		}

	}

} catch (e) {
	errorOccured = true;
	var errorMessage = 'Error occured in add checkboxes for Installed On to cert requests: ' + e;
	outputLog += errorMessage;
	gs.info(errorMessage);
} finally {
	if (errorOccured) {
		gs.info('Add checkboxes for Installed On to cert requests finished with Errors');
		outputLog += 'Add checkboxes for Installed On to cert requests finished with Errors';
	} else {
		gs.info('Add checkboxes for Installed On to cert requests finished without Errors');
		outputLog += 'Add checkboxes for Installed On to cert requests finished without Errors';
	}
}

gs.info(outputLog);
