sys_script_fix.script:
var gr = new GlideRecord('x_unugr_certificat_digital_certificate');
gr.addQuery('u_expires_in_days=0^ORu_expires_in_days=NULL^u_active=true');
gr.setLimit(10);
gr.query();
while(gr.next()){

    gr.setWorkflow(false);
    //gr.setForceUpdate(true);
    gr.autoSysFields(false);
    gr.u_active = false;
    gr.update();

}
