		//First See if we can find the certificate by serial number
		var currentCertificateSysID = "";
		currentCertificateSysID = certificateLookupBySerialNumber(serialNumber);
		//if certificate isnt found see if we can make a match based on commonname, validFrom and validTo
		//current external ssl vendor does not supply the actual serial number of the certificate so we need to
		//try and find a match.  If we make the match update the current record with the actual serial number
		
		if(currentCertificateSysID === '' && issuerOrganizationName == vendorSource) {
			currentCertificateSysID = certificateVendorLookupByCommonName(subjectCommonName,validTo);
			//if we found it see if we need to update any other data on the record, since the vendor supplies
			//very little data
			if(currentCertificateSysID !== ""){
				checkUpdateVendorCertificate(currentCertificateSysID,serialNumber,signatureAlgorithm,SANs,ExtendedKeyUsage,subjectOrganizationName);			
			}
		}else if (currentCertificateSysID == '' && issuerOrganizationName != vendorSource && calcDaysDiffFromToday(convertToCommonDate(validTo)) > -ignoreExpiredDays){
			//Certificate not found and is not from our External Vendor and its expiration Date was not greate than 40 days ago then Add to Cert Repository
			certificateRecord = new GlideRecord('x_unugr_certificat_digital_certificate');
			certificateRecord.initialize();
			
			certificateRecord.u_subject = subjectOrganizationName;
			certificateRecord.u_source = scanSource;
			certificateRecord.u_signature_algorithm = signatureAlgorithm;
			certificateRecord.u_short_description = ExtendedKeyUsage;
			certificateRecord.u_serial_number = serialNumber;
			certificateRecord.u_sans = SANs;
			certificateRecord.u_issuer = issuerOrganizationName;
			certificateRecord.u_common_name = subjectCommonName;
			certificateRecord.u_environment = findEnvironmentByCommonNameOrSans(subjectCommonName,SANs);
			certificateRecord.u_certificate_effective_date = convertToCommonDate(validFrom);
			certificateRecord.u_expires = convertToCommonDate(validTo);
			currentCertificateSysID = certificateRecord.insert();
			certificateRecord += 1;
			
		}
		
		//now that we have either found or inserted the certificate we will either update an existing relationship to a ci or create a relationship
		// if we couldnt match the CI that a cert is associated with we will need it to the CI Not matched table for follow-up
		if(currentCertificateSysID != '' && currentServerSysID != '' ){
			//first see if the relationship already exists, if it does update the last found date
			//if it doesnt then add it
// 			workflow.info('Relationship Server ' + currentServerSysID + ' Certificate ' + currentCertificateSysID);
			var queryString = 'server=' + currentServerSysID + '^digital_certificate=' + currentCertificateSysID; 
			var relationshipRecord =  new GlideRecord('x_unugr_certificat_m2m_servers_digital_cert');
			relationshipRecord.addEncodedQuery(queryString);
			relationshipRecord.query();
			
			if(relationshipRecord.next()){
				relationshipRecord.u_last_found = lastDetected;
				if(!firstDetected){
					relationshipRecord.u_first_found = lastDetected;
				}
				relationshipRecord.update();
				relationshipUpdated += 1;
			} else {
				relationshipRecord.initialize();
				relationshipRecord.digital_certificate = currentCertificateSysID;
				relationshipRecord.server = currentServerSysID;
				relationshipRecord.u_first_found = lastDetected;
				relationshipRecord.u_last_found = lastDetected;
				relationshipRecord.insert();
				relationshipInserted += 1;				
			}
		} else if(currentCertificateSysID != '' && currentServerSysID == '') {
			var missingCIRecord = new GlideRecord('x_unugr_certificat_scan_ci_not_found');
			
			var queryString ='ci_name=' + dnsEntry + '^ORci_netbios=' + netBios + '^ORci_ip_address=' + ipAddress + '^digital_certificate=' + currentCertificateSysID ;
			
			missingCIRecord.addEncodedQuery(queryString);
			missingCIRecord.query();
			
			if(missingCIRecord.getRowCount() == 0){
				missingCIRecord.initialize();
				missingCIRecord.ci_name = dnsEntry;
				missingCIRecord.ci_netbios = netBios;
				missingCIRecord.ci_ip_address = ipAddress;
				missingCIRecord.digital_certificate = currentCertificateSysID;
				missingCIRecord.insert();				
			}
			cimissing += 1;
		} else{
			recordsIgnored += 1;
		}
			
			
		
// 		if(debugOutput){
// 			if(currentServerSysID !== ""){
// 				workflow.info('Server Found: ' + dnsEntry + ' ' + currentServerSysID);
// 			} else {
// 				workflow.info('Server Not Found: ' + ipAddress + ' ' + dnsEntry);
// 			}
// 			if(currentCertificateSysID !== ""){
// 				workflow.info('***Certificate Found***: ' + currentCertificateSysID);
// 			}
// 			else{
// 				workflow.info('Certificate Not Found for Serial ' + serialNumber);
// 			}
// 		}
	}
} catch(e) {
	errorOccured = true;
	processStatus = 'Failure';
	if(/null/.test(e.message)){//Error Message is null
		errorMessage = 'Error Occured while parsing certificate scan data. Last processed IPAddresss: ' + ipAddress;
		
	} else {
		errorMessage = 'Error Occured while parsing certificate scan data. Last processed IPAddresss: ' + ipAddress + 'Reason: ' + e.message;
	}
} finally {
	var statusDescription = '';
	//if error occured then log the error in the import table
	if(errorOccured) {
		statusDescription = errorMessage;
	} else {
		workflow.scratchpad.certificateScanCount += certificateScanCount;
		workflow.scratchpad.relationshipUpdated += relationshipUpdated;
		workflow.scratchpad.relationshipInserted += relationshipInserted;
		workflow.scratchpad.recordsIgnored += recordsIgnored;

	}
	var endingTime = new GlideDateTime();
	var scanLogRecord = new GlideRecord('x_unugr_certificat_certificate_scan_log');
	
	scanLogRecord.addQuery('sys_id', '=', workflow.scratchpad.currentLogRecord);
	scanLogRecord.query();
	var fileProcessing = 0;
	fileProcessing = workflow.scratchpad.counter + 1;
	statusDescription =  'Processing ' + workflow.scratchpad.currentTenableInputFile + ' File number ' + fileProcessing;
	
	while (scanLogRecord.next()) {
		scanLogRecord.u_status = 'Processing';
		scanLogRecord.u_status_description = statusDescription;
		scanLogRecord.u_scan_ended = endingTime;
		scanLogRecord.update();
	}
}

//Functions
function serverLookupByName(serverName){
	var foundSysId = "";
	
	try{
		if(serverName.indexOf('.') >= 0) {
			serverName = serverName.substring(0,serverName.indexOf('.'));
		}

		var serverLookupRecord = new GlideRecord('cmdb_ci_server');
		serverLookupRecord.addQuery('name','=',serverName);
		serverLookupRecord.query();

		if(serverLookupRecord.getRowCount() != 0){
			while(serverLookupRecord.next()){
				foundSysId = serverLookupRecord.sys_id; 
			}
		}		
	}catch(e){
		throw(e);
	}
	
	return foundSysId;
	
}

function serverLookupByIP(IPAddress){
	var foundSysId = "";
	
	try {
		
		var serverLookupRecord = new GlideRecord('cmdb_ci_server');
		serverLookupRecord.addQuery('ip_address','=',IPAddress);
		serverLookupRecord.query();

		if(serverLookupRecord.getRowCount() != 0){
			while(serverLookupRecord.next()){
				foundSysId = serverLookupRecord.sys_id; 
			}
		}

	}catch(e){
		throw(e);
	}
	
	return foundSysId;	
}

function certificateLookupBySerialNumber(serialNumber){
	var foundSysId = "";
	
	try {
		
		var certificateLookupRecord = new GlideRecord('x_unugr_certificat_digital_certificate');
		certificateLookupRecord.addQuery('u_serial_number','=',serialNumber);
		certificateLookupRecord.query();
		
		if(certificateLookupRecord.getRowCount() != 0){
			while(certificateLookupRecord.next()){
				foundSysId = certificateLookupRecord.sys_id;
			}
		}
		
		
	}catch(e){
		throw(e);
	}
	return foundSysId;	
}

function certificateVendorLookupByCommonName(subjectCommonName,validTo){
	var foundSysId = "";
	
	try {
		
		var dateTo = convertToCommonDate(validTo);
		
		var certificateLookupRecord = new GlideRecord('x_unugr_certificat_digital_certificate');
		var encodedQuery = "u_common_name=" + subjectCommonName + "^u_expiresON" + dateTo + "@javascript:gs.dateGenerate('" + dateTo + "','start')@javascript:gs.dateGenerate('" + dateTo + "','end')";
		
		certificateLookupRecord.addEncodedQuery(encodedQuery);
		certificateLookupRecord.query();
		
// 		workflow.info(subjectCommonName + ' before ' + validTo + ' after ' + dateTo);
// 		workflow.info(encodedQuery);
		if(certificateLookupRecord.getRowCount() != 0){
			while(certificateLookupRecord.next()){
				foundSysId = certificateLookupRecord.sys_id;
			}
		}
		
	}catch(e){
		throw(e);
	}
	return foundSysId;	
	
}

function checkUpdateVendorCertificate(currentCertificateSysID,serialNumber,signatureAlgorithm,SANs,ExtendedKeyUsage,subjectOrganizationName){
	var doUpdate = false;
	try {
		
		var certificateLookupRecord = new GlideRecord('x_unugr_certificat_digital_certificate');
		certificateLookupRecord.addQuery('sys_id','=',currentCertificateSysID);
		certificateLookupRecord.query();
		
		if(certificateLookupRecord.getRowCount() != 0){
			while(certificateLookupRecord.next()){
				if(certificateLookupRecord.u_serial_number == ''){
					certificateLookupRecord.u_serial_number = serialNumber;
					doUpdate = true;
				}
				if(certificateLookupRecord.u_signature_algorithm == ''){
					certificateLookupRecord.u_signature_algorithm = signatureAlgorithm;
					doUpdate = true;
				}
				if(certificateLookupRecord.u_sans == ''){
					certificateLookupRecord.u_sans = SANs;
					doUpdate = true;
				}				
				if(certificateLookupRecord.u_subject == ''){
					certificateLookupRecord.u_subject = subjectOrganizationName;
					doUpdate = true;
				}							
				if(certificateLookupRecord.u_short_description == ''){
					certificateLookupRecord.u_short_description = ExtendedKeyUsage;
					doUpdate = true;
				}	
				
				if(doUpdate){
					certificateLookupRecord.update();
					certUpdated += 1;
				}
			}
		}
		
		
	}catch(e){
		throw(e);
	}	
}

function getMonthFromString(mon,year){
   return new Date(Date.parse(mon +" 1, " + year)).getMonth()+1;
}

function convertToCommonDate(dateTestString){
    var dateArray = dateTestString.split(' ');
    var yearStr = dateArray[3];
    var dayStr = dateArray[1];
    var monStr = getMonthFromString(dateArray[0],yearStr);
    var timeStr = dateArray[2];
    
    var dateString = monStr + '/' + dayStr + '/' + yearStr + ' ' + timeStr + ' GMT';
    
    var date = new Date(dateString);
    
    return formatDate(date);
}

function formatDate(date) {
    var d = new Date(date),
        month = '' + (d.getMonth() + 1),
        day = '' + d.getDate(),
        year = d.getFullYear();

    if (month.length < 2) month = '0' + month;
    if (day.length < 2) day = '0' + day;

    return [year, month, day].join('-');
}

function findEnvironmentByCommonNameOrSans(subjectCommonName,SANs){
	var environment;
	var commonName = subjectCommonName.toUpperCase();
	var alternateName = SANs.toUpperCase();
	var acceptance = 'ACPT';
	var development = 'DEV';
	var production = 'CORP';
	
	if(commonName.includes('.' + development) || alternateName.includes('.' + development)  || 
		commonName.includes(' ' + development + ' ') || alternateName.includes(' ' + development + ' ') ){
		environment = 'DEV';
	} else if(commonName.includes('.' + acceptance) || alternateName.includes('.' + acceptance)  || 
		commonName.includes(' ' + acceptance + ' ') || alternateName.includes(' ' + acceptance + ' ') ){
		environment = 'UAT';
	} else if(commonName.includes('.' + production) || alternateName.includes('.' + production)  || 
		commonName.includes(' ' + production + ' ') || alternateName.includes(' ' + production + ' ') ){
		environment = 'PRODUCTION';
	} else{
		environment = '';
	}
	
	return environment;
}

function calcDaysDiffFromToday(dateString){
	var todayDate = new GlideDateTime();
	var compareDate = new GlideDateTime(dateString);
	var duration = GlideDate.subtract(todayDate,compareDate);
	var diffDays = duration.getRoundedDayPart();

	if(debugOutput){
		gs.info('Date is ' + dateString + ' Converted Date is: ' + compareDate + ' Days Diff is: ' + diffDays);
	}
	return diffDays;
}
